function accordion(){document.querySelectorAll(".accordion__item-trigger").forEach((t=>{t.addEventListener("click",(()=>{const e=t.parentNode;e.classList.contains("accordion__item-active")?e.classList.remove("accordion__item-active"):(document.querySelectorAll(".accordion__item").forEach((t=>t.classList.remove("accordion__item-active"))),e.classList.add("accordion__item-active"))}))}))}accordion();const btn=document.querySelector(".header__copy-img"),tooltip=document.querySelector(".tooltip");function copyText(){const t=document.querySelector(".copy__input");navigator.clipboard.writeText(t.value).then((()=>{})).catch((t=>{console.log("Something went wrong",t)}))}btn.addEventListener("click",(()=>{copyText(),tooltip.style.opacity=1,setTimeout((()=>{tooltip.style.opacity=0}),3e3)}));