function accordion(){document.querySelectorAll(".accordion__item-trigger").forEach((e=>{e.addEventListener("click",(()=>{const t=e.parentNode;t.classList.contains("accordion__item-active")?t.classList.remove("accordion__item-active"):(document.querySelectorAll(".accordion__item").forEach((e=>e.classList.remove("accordion__item-active"))),t.classList.add("accordion__item-active"))}))}))}accordion();const btn=document.querySelector(".header__copy-img"),tooltip=document.querySelector(".tooltip");function copyText(){document.querySelector(".copy__input").select(),document.execCommand("copy")}btn.addEventListener("click",(()=>{copyText(),tooltip.style.opacity=1,setTimeout((()=>{tooltip.style.opacity=0}),3e3)}));